import React, { Component } from 'react'
import {Image,Avatar} from "antd"
import "./index.css"
import {Link} from "react-router-dom"
import {UserOutlined,SearchOutlined} from '@ant-design/icons'
export default class index extends Component {
    state={
        logo:"https://img.onlinedown.net/download/202102/155443-6030c043ca7ab.jpg",
        error:"data:image/png;base64,",
        avatar:"http://img.duoziwang.com/2019/02/03300852112692.jpg"
   
    }
    handleClick = () => {
        this.setState({isUser:false,username:''})
        localStorage.setItem('username',null)
        localStorage.setItem('isUser',false)
    }

    componentDidMount(){
        const userData=[
            {username:"lijie",passwd:"lijie"},
            {username:"liuliang",passwd:"liuliang"},
            {username:"wangkang",passwd:"wangkang"},
            {username:"hongjiasheng",passwd:"hongjiasheng"},
            {username:"liuhongyang",passwd:"liuhongyang"},
            {username:"tangxiang",passwd:"tangxiang"},
        ]
        const ledata=localStorage.getItem("userData")
        if(!ledata){
            localStorage.setItem("userData",JSON.stringify(userData))
        }

        const {username,isUser} = this.state
        const usernames = localStorage.getItem('username')
        const isUsers = localStorage.getItem('isUser')
        if(isUsers){
            this.setState({isUser:true,username:usernames})
        }else{
            localStorage.setItem('username',username)
            localStorage.setItem('isUser',isUser)
        }
    }

    render() {
        return (
            <div className="header_wrapper">
                <div className="header">
                   <Image
                    width={44}
                    src={this.state.logo}
                    fallback={this.state.error}
                    className="logo"
                    preview={false}
                    />
                <div className="header_wrap">
                    <div className="name">康康音乐</div>
                    
                </div>
                <div className="user_wrap">
                    
{/*                     
                      {
                          sessionStorage.getItem("user")!=null
                          ?
                          <div className="user_state">
                                 <Avatar src={this.state.avatar} size="small" className="avatar" />
                                    <span>退出</span>
                            </div>  
                          :
                          <div className="user_state">
                                <Avatar size="small" style={{ backgroundColor: 'green' }} icon={<UserOutlined />} className="avatar" />
                                <span>/ 注销</span>
                          </div>
                      } */}
                      
                      {
                          this.state.isUser
                          ?
                          <div className="user_state">
                                 <Avatar src={this.state.avatar} size="small" className="avatar" />
                                 <span>你好，{this.state.username}</span>
                                 <Link to="/" onClick={this.handleClick}>退出</Link>
                            </div>  
                          :
                          <div className="user_state">
                                <Avatar size="small" style={{ backgroundColor: '#1890ff' }} icon={<UserOutlined />} className="avatar" />     
                                <Link to="/User/Login">登录</Link>
                                </div>
                      }
                    </div>
                </div>
                {this.state.isUser
                          ?
                          <nav>
                          <Link to="/recommend">推荐</Link>
                          <Link to="/rank">排行</Link>
                          <Link to="/input"><SearchOutlined /></Link>
                      </nav>
                    :
                    <nav>
                        <Link to="/User/Login">登录</Link>
                        <Link to="/User/Register">注册</Link>
                        {/* //王康 */}
                    </nav>
                    }
                    {/* <nav>
                        <Link to="/recommend">推荐</Link>
                        <Link to="/rank">排行</Link>
                        <Link to="/serch"><SearchOutlined /></Link>
                    </nav> */}
                </div>
        )
    }
}
